              <div className="text-2xl font-bold text-green-600">
                {currencySymbol} {totalRoundUps.toFixed(2)}
              </div>
              <div className="text-sm text-muted-foreground">{t.totalRoundUps}</div>
            </div>
            <div className="text-center">
